mapper.insert:
INSERT INTO TB_BOARD
	( IDX
	  , TITLE
	  , CONTENTS
	  , COUNT
	  , WRITER
	  , INDATE )
VALUES ( NULL
	  , #{title, jdbcType=VARCHAR}
	  , #{contents, jdbcType=VARCHAR}
	  , #{count, jdbcType=VARCHAR}
	  , #{writer, jdbcType=VARCHAR}
	  , SYSDATE() )

mapper.update:
UPDATE TB_BOARD
SET TITLE=#{title, jdbcType=VARCHAR}
	, CONTENTS=#{contents, jdbcType=VARCHAR}
WHERE IDx=#{idx}

UPDATE TB_BOARD
SET COUNT=IFNULL(COUNT,0)+1
WHERE IDx=#{idx}

mapper.select:
SELECT A.IDX as idx , A.TITLE as title , A.CONTENTS as contents , IFNULL(A.COUNT,0) as count , A.WRITER as writer , A.INDATE as indate , B.USER_NAME as writerNm , (SELECT COUNT(*) FROM TB_REPLY X WHERE X.IDX = A.IDX) AS reply , (SELECT X.FILENAME FROM TB_ATTACH X WHERE X.IDX = A.IDX AND X.SEQ=1) AS filename FROM TB_BOARD A LEFT JOIN TB_USER B ON A.WRITER = B.USER_ID WHERE 1=1
AND (A.TITLE LIKE CONCAT('%',#{searchKeyword},'%') OR A.CONTENTS LIKE CONCAT('%',#{searchKeyword},'%') )
ORDER BY A.IDX DESC LIMIT #{firstIndex}, #{recordCountPerPage}

SELECT USER_NAME
FROM TB_USER
WHERE 1=1
AND USER_ID = #{userId}
AND	PASSWORD = #{password}

mapper.delete:
DELETE FROM TB_ATTACH
WHERE 1=1
AND	IDX = #{idx}
AND	SEQ = #{seq}
